import React, { useEffect, useState } from 'react';
import { Avatar, Button, Dialog, DialogActions, DialogContent, DialogTitle, TextField } from '@mui/material';
import KeyboardBackspaceIcon from '@mui/icons-material/KeyboardBackspace';
import { useNavigate } from 'react-router-dom';
import BusinessCenterIcon from '@mui/icons-material/BusinessCenter';
import LocationOnIcon from '@mui/icons-material/LocationOn';
import CalendarMonthIcon from '@mui/icons-material/CalendarMonth';
import Box from '@mui/material/Box';
import Tab from '@mui/material/Tab';
import TabContext from '@mui/lab/TabContext';
import TabList from '@mui/lab/TabList';
import TabPanel from '@mui/lab/TabPanel';
import PostCard from '../HomeSection/PostCard';
import { useDispatch, useSelector } from 'react-redux';
import { getUsersPost } from '../../Store/Post/Action';
import { updateUserProfile } from '../../Store/Auth/Action';

function Profile() {
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const { auth, post } = useSelector(store => store);
    const [selectedPost, setSelectedPost] = useState(null);


    const [openModal, setOpenModal] = useState(false);
    const [formData, setFormData] = useState({
        fullName: auth.user?.fullName || '',
        location: auth.user?.location || '',
        website: auth.user?.website || '',
        birthDate: auth.user?.birthDate || '',
        password: '',
        mobile: auth.user?.mobile || '',
        image: auth.user?.image || '',
        backgroundImage: auth.user?.backgroundImage || '',
        bio: auth.user?.bio || '',
    });

    const handleBack = () => {
        navigate(-1);
    };

    const handleOpenProfileModal = () => {
        setOpenModal(true);
    };

    const handleCloseProfileModal = () => {
        setOpenModal(false);
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleSubmit = () => {
        dispatch(updateUserProfile(formData));
        setOpenModal(false);
    };

    const [value, setValue] = useState('1');

    const handleTabChange = (event, newValue) => {
        setValue(newValue);
    };

    useEffect(() => {
        dispatch(getUsersPost(auth.user?.id));
        console.log(post.posts);
    }, [dispatch, auth.user?.id]);

    return (
        <div>
            <section className="z-50 flex items-center sticky top-0 bg-opacity-95">
                <KeyboardBackspaceIcon className="cursor-pointer" onClick={handleBack} />
                <h1 className="py-5 text-xl font-bold opacity-90 ml-5">{auth.user?.fullName}</h1>
            </section>

            <section>
                <img
                    className="w-[100%] h-[15rem] objective-cover"
                    src="https://www.anhrgroup.com/sites/default/files/styles/inner_pages_slideshow/public/basic-pages/al-nahda-samar-restaurant-1.jpg?itok=GkrSRo1S"
                    alt=""
                />
            </section>
            <section className="pl-6">
                <div className="flex justify-between items-start mt-5 h-[5rem]">
                    <Avatar
                        className="transform -translate-y-24"
                        alt="avatar"
                        src="https://cdn-icons-png.flaticon.com/512/8345/8345328.png"
                        sx={{ width: '10rem', height: '10rem', border: '4px solid white' }}
                    />
                    <Button className="rounded-full" variant="contained" sx={{ borderRadius: '20px' }} onClick={handleOpenProfileModal}>
                        Edit profile
                    </Button>
                </div>
                <div>
                    <div className="flex items-center">
                        <h1 className="font-bold text-lg">{auth.user?.fullName}</h1>
                        <img className="ml-2 w-5 h-5" src="https://cdn-icons-png.flaticon.com/512/6364/6364343.png" alt="content-image" />
                    </div>
                    <h1 className="text-gray-500">@{auth.user?.fullName ? auth.user.fullName.split(' ').join('_').toLowerCase() : 'unknown_user'}</h1>
                </div>
                <div className="mt-2 space-y-3">
                    <p>{auth.user?.bio}</p>
                    <div className="py-1 flex space-x-5">
                        <div className="flex items-center">
                            <BusinessCenterIcon />
                            <p className="ml-2">Education</p>
                        </div>
                        <div className="flex items-center">
                            <LocationOnIcon />
                            <p className="ml-2">{auth.user?.location}</p>
                        </div>
                        <div className="flex items-center">
                            <CalendarMonthIcon />
                            <p className="ml-2">{auth.user?.birthDate}</p>
                        </div>
                    </div>
                    <div className="flex items-center space-x-5">
                        <div className="flex items-center space-x-1 font-semibold">
                            <span>{auth.user?.following.length}</span>
                            <span className="text-gray-500">Following</span>
                        </div>
                        <div className="flex items-center space-x-1 font-semibold">
                            <span>{auth.user?.followers.length}</span>
                            <span className="text-gray-500">Followers</span>
                        </div>
                    </div>
                </div>
            </section>

            <section>
                <Box className="mt-4" sx={{ width: '100%', typography: 'body1' }}>
                    <TabContext value={value}>
                        <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
                            <TabList onChange={handleTabChange} aria-label="lab API tabs example">
                                <Tab label="Post" value="1" />
                                <Tab label="Image" value="2" />
                                <Tab label="Saved" value="3" />
                            </TabList>
                        </Box>
                        <TabPanel value="1">
                            {post.posts && post.posts.length > 0 ? (
                                post.posts.map((post) => <PostCard key={post.id} post={post} />)
                            ) : (
                                <p className="text-gray-500">No posts available.</p>
                            )}
                        </TabPanel>
                        <TabPanel value="2">
                            {post.posts && post.posts.length > 0 ? (
                                <div className="flex flex-wrap gap-4" onClick={() => handleOpenPostModal(post)}>
                                    {post.posts.map((post, index) => (
                                        post.image ? (
                                            <img
                                                key={index}
                                                src={post.image}
                                                alt={`Post Image ${index}`}
                                                className="w-32 h-32 object-cover"
                                            />
                                        ) : null
                                    ))}
                                </div>
                            ) : (
                                <p className="text-gray-500">No images available.</p>
                            )}
                        </TabPanel>
                        <TabPanel value="3">Item Three</TabPanel>
                    </TabContext>
                </Box>
            </section>

            {/* Modal for Editing Profile */}
            <Dialog open={openModal} onClose={handleCloseProfileModal}>
                <DialogTitle>Edit Profile</DialogTitle>
                <DialogContent>
                    <TextField
                        fullWidth
                        margin="normal"
                        label="Full Name"
                        variant="outlined"
                        name="fullName"
                        value={formData.fullName}
                        onChange={handleChange}
                    />
                    <TextField
                        fullWidth
                        margin="normal"
                        label="Location"
                        variant="outlined"
                        name="location"
                        value={formData.location}
                        onChange={handleChange}
                    />
                    <TextField
                        fullWidth
                        margin="normal"
                        label="Website"
                        variant="outlined"
                        name="website"
                        value={formData.website}
                        onChange={handleChange}
                    />
                    <TextField
                        fullWidth
                        margin="normal"
                        label="Birth Date"
                        variant="outlined"
                        name="birthDate"
                        value={formData.birthDate}
                        onChange={handleChange}
                    />
                    <TextField
                        fullWidth
                        margin="normal"
                        label="Mobile"
                        variant="outlined"
                        name="mobile"
                        value={formData.mobile}
                        onChange={handleChange}
                    />
                    <TextField
                        fullWidth
                        margin="normal"
                        label="Bio"
                        variant="outlined"
                        name="bio"
                        value={formData.bio}
                        onChange={handleChange}
                    />
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleCloseProfileModal} color="primary">
                        Cancel
                    </Button>
                    <Button onClick={handleSubmit} color="primary">
                        Save Changes
                    </Button>
                </DialogActions>
            </Dialog>
        </div>
    );
}

export default Profile;
