import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getReels, likeReel } from "../redux/actions/reelActions";
import { Button } from "@mui/material";
import { Favorite, FavoriteBorder } from "@mui/icons-material";

const Reel = () => {
    const dispatch = useDispatch();
    const { reels } = useSelector((state) => state.reel);
    const [currentIndex, setCurrentIndex] = useState(0);

    useEffect(() => {
        dispatch(getReels());
    }, [dispatch]);

    const handleLike = (id, liked) => {
        dispatch(likeReel(id)); // Gửi action thích/bỏ thích
    };

    const nextReel = () => {
        setCurrentIndex((prev) => (prev + 1) % reels.length);
    };

    return (
        <div className="flex flex-col items-center justify-center w-full h-screen bg-black text-white">
            {reels.length > 0 ? (
                <div className="relative w-[400px] h-[600px] bg-gray-900 rounded-lg overflow-hidden shadow-lg">
                    {/* Video */}
                    <video
                        src={reels[currentIndex]?.image}
                        controls
                        className="w-full h-full object-cover"
                    />

                    {/* Thông tin người đăng */}
                    <div className="absolute top-3 left-3 flex items-center gap-2">
                        <img
                            src={reels[currentIndex]?.user?.image || "https://via.placeholder.com/40"}
                            alt="Avatar"
                            className="w-10 h-10 rounded-full"
                        />
                        <div>
                            <p className="text-sm font-bold">{reels[currentIndex]?.user?.fullName}</p>
                            <p className="text-xs opacity-80">{new Date(reels[currentIndex]?.createdAt).toLocaleDateString()}</p>
                        </div>
                    </div>

                    {/* Nút thích và số lượt thích */}
                    <div className="absolute bottom-4 left-4 flex items-center gap-2">
                        <Button onClick={() => handleLike(reels[currentIndex].id, reels[currentIndex].liked)} color="error">
                            {reels[currentIndex].liked ? <Favorite /> : <FavoriteBorder />}
                        </Button>
                        <span>{reels[currentIndex]?.totalLikes} lượt thích</span>
                    </div>

                    {/* Nút chuyển sang reel tiếp theo */}
                    <Button
                        onClick={nextReel}
                        variant="contained"
                        color="primary"
                        className="absolute bottom-4 right-4"
                    >
                        Tiếp theo
                    </Button>
                </div>
            ) : (
                <p className="text-white text-center mt-40">Không có reel nào.</p>
            )}
        </div>
    );
};

export default Reel;
