import axios from "axios"
import { api, API_BASE_URL } from "../../config/api"
import { GET_USER_PROFILE_USER_FAILURE, GET_USER_PROFILE_USER_SUCCESS, LOGIN_USER_FAILURE, LOGIN_USER_SUCCESS, LOGOUT, REGISTER_USER_FAILURE, REGISTER_USER_SUCCESS } from "./ActionType";

export const loginUser = (loginData) => async (dispatch) => {
    try {
        const response = await axios.post(`${API_BASE_URL}/auth/login`, loginData);
        const data = response.data;
        if (data.jwt) {
            localStorage.setItem("jwt", data.jwt);
        }

        dispatch({ type: LOGIN_USER_SUCCESS, payload: data });
    } catch (error) {
        console.log(error);
        dispatch({ type: LOGIN_USER_FAILURE, payload: error.message });
    }
};

export const registerUser = (registerData) => async (dispatch) => {
    try {
        const response = await axios.post(`${API_BASE_URL}/auth/register`, registerData);
        const data = response.data;

        if (data.jwt) {
            localStorage.setItem("jwt", data.jwt);
        }

        dispatch({ type: REGISTER_USER_SUCCESS, payload: data.jwt });
    } catch (error) {
        console.log(error);
        dispatch({ type: REGISTER_USER_FAILURE, payload: error.message });
    }
};


export const getUserProfile = (jwt) => async (dispatch) => {
    try {
        const response = await axios.get(`${API_BASE_URL}/api/user/profile`, {
            headers: {
                "Authorization": `Bearer ${jwt}`
            }
        });


        dispatch({ type: GET_USER_PROFILE_USER_SUCCESS, payload: response.data });
    } catch (error) {
        console.log(error);
        dispatch({ type: GET_USER_PROFILE_USER_FAILURE, payload: error.message });
    }
}

export const findUserById = (userId) => async (dispatch) => {
    try {
        const { data } = await api.get(`/api/user/${userId}`);

        dispatch({ type: GET_USER_PROFILE_USER_SUCCESS, payload: response.data });
    } catch (error) {
        console.log(error);
        dispatch({ type: GET_USER_PROFILE_USER_FAILURE, payload: error.message });
    }
}

export const logout = () => async (dispatch) => {
    localStorage.removeItem("jwt");

    dispatch({ type: LOGOUT, payload: null });
}
