import { Avatar, Divider, Grid, IconButton } from '@mui/material';
import React, { useEffect, useState } from 'react';
import WestIcon from '@mui/icons-material/West';
import CallIcon from '@mui/icons-material/Call';
import VideocamIcon from '@mui/icons-material/Videocam';
import AddPhotoAlternateIcon from '@mui/icons-material/AddPhotoAlternate';
import SearchUser from '../SearchUser/SearchUser';
import SockJS from 'sockjs-client';
import Stomp from 'stompjs';
import { API_BASE_URL } from '../../config/api';
import SendIcon from '@mui/icons-material/Send';

function Message() {
    const [stompClient, setStompClient] = useState(null);
    const [messages, setMessages] = useState([]);
    const [inputMessage, setInputMessage] = useState('');

    useEffect(() => {
        const sock = new SockJS(`${API_BASE_URL}/ws`);
        const stomp = Stomp.over(sock);
        stomp.connect({}, () => {
            console.log("Connected to WebSocket");
            stomp.subscribe('/topic/messages', (msg) => {
                const newMessage = JSON.parse(msg.body);
                setMessages(prevMessages => [...prevMessages, newMessage]);
            });
        });
        setStompClient(stomp);
    }, []);

    const sendMessage = () => {
        if (stompClient && inputMessage.trim() !== '') {
            const message = {
                sender: "Nguyễn Khánh Duy",
                receiver: "Trần Minh Tú",
                content: inputMessage,
                timestamp: new Date().toLocaleTimeString()
            };
            stompClient.send("/app/chat", {}, JSON.stringify(message));
            setInputMessage('');
        }
    };

    return (
        <div>
            <Grid container className='h-screen overflow-y-hidden'>
                <Grid item xs={3} className='px-5'>
                    <div className='flex h-full justify-between space-x-2'>
                        <div className='w-full'>
                            <div className='flex space-x-4 items-center py-5'>
                                <WestIcon />
                                <h1 className='text-xl font-bold'>Trang chủ</h1>
                            </div>
                            <div className='h-[83vh]'>
                                <SearchUser />
                                <div className='h-full space-y-4 mt-5 overflow-y-auto hideScrollbar'>
                                </div>
                            </div>
                        </div>
                    </div>
                </Grid>
                <Grid item xs={9} className='h-full'>
                    <div className='flex justify-between items-center border-l p-5'>
                        <div className='flex items-center space-x-3'>
                            <Avatar src="https://cdn-icons-png.flaticon.com/512/8345/8345328.png" alt="avatar" />
                            <p>Nguyễn Khánh Duy</p>
                        </div>
                        <div className='flex space-x-3'>
                            <IconButton><CallIcon /></IconButton>
                            <IconButton><VideocamIcon /></IconButton>
                        </div>
                    </div>
                    <div className='hideScrollbar overflow-y-scroll h-[82vh] px-2 space-y-5 py-5'>
                        {messages.map((message, index) => (
                            <div key={index} className={`flex ${message.sender === "Nguyễn Khánh Duy" ? 'justify-start' : 'justify-end'} space-x-3 py-2`}>
                                <Avatar src="https://cdn-icons-png.flaticon.com/512/8345/8345328.png" alt={message.sender} />
                                <div className={`max-w-[70%] px-4 py-2 rounded-lg ${message.sender === "Nguyễn Khánh Duy" ? 'bg-blue-500 text-white' : 'bg-gray-200 text-black'}`}>
                                    <p>{message.content}</p>
                                    <span className='text-xs text-black'>{message.timestamp}</span>
                                </div>
                            </div>
                        ))}
                    </div>
                    <div className='sticky bottom-0 border-1'>
                        <div className='py-5 flex items-center justify-center space-x-5'>
                            <input
                                type="text"
                                className='bg-transparent border border-[#3b40544] rounded-full w-[90%] py-3 px-5'
                                placeholder='Nhập tin nhắn...'
                                value={inputMessage}
                                onChange={(e) => setInputMessage(e.target.value)}
                            />
                            <IconButton onClick={sendMessage}><SendIcon /></IconButton>
                        </div>
                    </div>
                </Grid>
            </Grid>
        </div>
    );
}

export default Message;
