import { Avatar, Grid, IconButton } from '@mui/material';
import React, { useEffect, useState, useCallback } from 'react';
import WestIcon from '@mui/icons-material/West';
import CallIcon from '@mui/icons-material/Call';
import VideocamIcon from '@mui/icons-material/Videocam';
import SendIcon from '@mui/icons-material/Send';
import { useDispatch, useSelector } from 'react-redux';
import { getHistoyMessage, getUser, sendMessage } from '../../Store/Chat/Action';
import SearchUser from '../SearchUser/SearchUser';
import SockJS from 'sockjs-client';
import { API_BASE_URL } from '../../config/api';
import Stomp from 'stompjs';

function Message() {
    const dispatch = useDispatch();
    const auth = useSelector((state) => state.auth.user);
    const loading = useSelector((state) => state.chat.loading);
    const users = useSelector((state) => state.chat.users);
    const [messages, setMessages] = useState([]);
    const [inputMessage, setInputMessage] = useState('');
    const [stompClient, setStompClient] = useState(null);
    const [userId, setUserId] = useState(null);

    useEffect(() => {
        dispatch(getUser());
        dispatch(getHistoyMessage(auth.id === 1 ? 2 : 1));
    }, [dispatch, auth.id]);

    useEffect(() => {
        const sock = new SockJS(`${API_BASE_URL}/ws`);
        const stomp = Stomp.over(sock);

        stomp.connect({}, () => {
            console.log("WebSocket connected");
            setStompClient(stomp);
        }, (error) => {
            console.error("WebSocket connection error:", error);
        });

        return () => {
            if (stomp && stomp.connected) {
                stomp.disconnect(() => console.log("WebSocket disconnected"));
            }
        };
    }, []);

    useEffect(() => {
        if (stompClient && auth) {
            const subscription = stompClient.subscribe(`/user/${auth.id}/private`, (message) => {
                const newMessage = JSON.parse(message.body);
                setMessages((prevMessages) => [...prevMessages, newMessage]);
            });
            return () => subscription.unsubscribe();
        }
    }, [stompClient, auth]);

    const handleSendMessage = useCallback(() => {
        if (!stompClient || !stompClient.connected) {
            console.warn("Không thể gửi tin nhắn: WebSocket chưa kết nối!");
            return;
        }

        if (inputMessage.trim()) {
            const messageData = {
                senderId: auth.id,
                receiverId: userId,
                content: inputMessage,
                timestamp: new Date().toISOString(),
            };
            stompClient.send(`/app/chat/${userId}`, {}, JSON.stringify(messageData));
            setMessages((prev) => [...prev, messageData]);
            dispatch(sendMessage(messageData));
            setInputMessage('');
        }
    }, [stompClient, inputMessage, auth.id, userId, dispatch]);

    return (
        <div>
            <Grid container className='h-screen overflow-y-hidden'>
                <Grid item xs={3} className='px-5'>
                    <div className='flex h-full flex-col'>
                        <div className='flex space-x-4 items-center py-5'>
                            <WestIcon />
                            <h1 className='text-xl font-bold'>Trang chủ</h1>
                        </div>
                        <SearchUser />
                        <div className='overflow-y-auto flex-1 hideScrollbar'>
                            {users?.length ? users.map((user) => (
                                <div key={user.id} className="flex items-center space-x-3 p-3 border-b cursor-pointer hover:bg-gray-200"
                                    onClick={() => setUserId(user.id)}>
                                    <Avatar src={user.image || "https://cdn-icons-png.flaticon.com/512/8345/8345328.png"} alt={user.fullName} />
                                    <p>{user.fullName}</p>
                                </div>
                            )) : (
                                <p className='text-center text-gray-500'>Không có người dùng nào</p>
                            )}
                        </div>
                    </div>
                </Grid>

                <Grid item xs={9} className='h-full flex flex-col'>
                    <div className='flex justify-between items-center border-l p-5'>
                        <div className='flex items-center space-x-3'>
                            <Avatar src="https://cdn-icons-png.flaticon.com/512/8345/8345328.png" alt="avatar" />
                            <p>Nguyễn Khánh Duy</p>
                        </div>
                        <div className='flex space-x-3'>
                            <IconButton><CallIcon /></IconButton>
                            <IconButton><VideocamIcon /></IconButton>
                        </div>
                    </div>

                    <div className='overflow-y-scroll flex-1 px-2 space-y-5 py-5 hideScrollbar'>
                        {messages.length ? messages.map((message, index) => (
                            <div key={index} className={`flex ${message.senderId === auth.id ? 'justify-end' : 'justify-start'} space-x-3 py-2`}>
                                {message.senderId !== auth.id && <Avatar src="https://cdn-icons-png.flaticon.com/512/8345/8345328.png" alt="receiver-avatar" />}
                                <div className={`max-w-[70%] px-4 py-2 rounded-lg ${message.senderId === auth.id ? 'bg-blue-500 text-white' : 'bg-gray-200 text-black'}`}>
                                    <p>{message.content}</p>
                                    <span className='text-xs text-black'>{new Date(message.timestamp).toLocaleTimeString()}</span>
                                </div>
                                {message.senderId === auth.id && <Avatar src="https://cdn-icons-png.flaticon.com/512/8345/8345328.png" alt="sender-avatar" />}
                            </div>
                        )) : (
                            <p className='text-center text-gray-500'>Chưa có tin nhắn nào.</p>
                        )}
                    </div>

                    <div className='border-t p-3 flex items-center space-x-5'>
                        <input
                            type="text"
                            className='flex-1 border rounded-full py-2 px-4'
                            placeholder='Nhập tin nhắn...'
                            value={inputMessage}
                            onChange={(e) => setInputMessage(e.target.value)}
                        />
                        <IconButton onClick={handleSendMessage}><SendIcon /></IconButton>
                    </div>
                </Grid>
            </Grid>
        </div>
    );
}

export default Message;
