import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { createReel, getReels } from "../../Store/Reel/Action";
import { Button, CircularProgress, Modal, TextField } from "@mui/material";
import { ArrowBackIos, ArrowForwardIos } from "@mui/icons-material";

const Reel = () => {
    const dispatch = useDispatch();
    const { reels, loading, error } = useSelector((state) => state.reel);

    const [showCreateForm, setShowCreateForm] = useState(true);
    const [file, setFile] = useState(null);
    const [description, setDescription] = useState("");
    const [currentIndex, setCurrentIndex] = useState(0);
    const [isModalOpen, setIsModalOpen] = useState(false);

    useEffect(() => {
        dispatch(getReels());
    }, [dispatch]);

    const handleCreateReel = () => {
        if (!file) return alert("Vui lòng chọn file!");

        const formData = new FormData();
        formData.append("file", file);
        formData.append("description", description);

        dispatch(createReel({ file, description }));
        setShowCreateForm(false);
    };

    const handleNext = () => {
        if (reels.length > 0 && currentIndex < reels.length - 1) {
            setCurrentIndex((prev) => prev + 1);
        }
    };

    const handlePrev = () => {
        if (currentIndex > 0) {
            setCurrentIndex((prev) => prev - 1);
        }
    };

    if (loading) return <CircularProgress />;
    if (error) return <p className="text-red-500">Lỗi: {error}</p>;

    return (
        <div className="relative w-full h-screen flex items-center justify-center bg-black">
            {showCreateForm ? (
                <div className="bg-white p-6 rounded-lg shadow-lg w-[400px]">
                    <h2 className="text-xl font-bold mb-4">Tạo Reel mới</h2>
                    <input
                        type="file"
                        onChange={(e) => setFile(e.target.files[0])}
                        className="mb-4 p-2 border w-full"
                    />
                    <TextField
                        label="Mô tả Reel..."
                        multiline
                        rows={3}
                        fullWidth
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                    />
                    <Button onClick={handleCreateReel} variant="contained" color="primary" className="mt-4 w-full">
                        Tạo Reel
                    </Button>
                </div>
            ) : (
                <div className="relative w-full h-screen flex items-center justify-center">
                    <div className="absolute inset-0 bg-black opacity-50"></div>

                    {reels.length > 0 ? (
                        <>
                            <div
                                className="relative w-[400px] h-[600px] bg-white rounded-lg overflow-hidden shadow-lg cursor-pointer"
                                onClick={() => setIsModalOpen(true)}
                            >
                                {reels[currentIndex]?.type === "video" ? (
                                    <video
                                        src={reels[currentIndex]?.src}
                                        controls
                                        className="w-full h-full object-cover"
                                    />
                                ) : (
                                    <img
                                        src={reels[currentIndex]?.src}
                                        alt="Reel"
                                        className="w-full h-full object-cover"
                                    />
                                )}
                            </div>

                            {currentIndex > 0 && (
                                <Button
                                    onClick={handlePrev}
                                    className="absolute left-4 bg-gray-500 text-white p-2 rounded-full"
                                >
                                    <ArrowBackIos />
                                </Button>
                            )}
                            {currentIndex < reels.length - 1 && (
                                <Button
                                    onClick={handleNext}
                                    className="absolute right-4 bg-gray-500 text-white p-2 rounded-full"
                                >
                                    <ArrowForwardIos />
                                </Button>
                            )}
                        </>
                    ) : (
                        <p className="text-white text-center mt-40">Không có reel nào.</p>
                    )}
                </div>
            )}

            <Modal open={isModalOpen} onClose={() => setIsModalOpen(false)}>
                <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-75">
                    <div className="relative w-[400px] h-[600px] bg-white rounded-lg overflow-hidden shadow-lg">
                        {reels.length > 0 && reels[currentIndex]?.type === "video" ? (
                            <video
                                src={reels[currentIndex]?.src}
                                controls
                                className="w-full h-full object-cover"
                            />
                        ) : (
                            <img
                                src={reels[currentIndex]?.src}
                                alt="Reel"
                                className="w-full h-full object-cover"
                            />
                        )}
                    </div>
                </div>
            </Modal>
        </div>
    );
};

export default Reel;
