import React, { useEffect, useState } from "react";
import { Button, TextField, Card, CardContent, Typography } from "@mui/material";
import { useDispatch, useSelector } from "react-redux";
import { getGroups, createGroup, joinGroup } from "../actions/groupActions";

const Groups = () => {
    const dispatch = useDispatch();
    const { groups, loading } = useSelector((state) => state.group);
    const [groupName, setGroupName] = useState("");

    useEffect(() => {
        dispatch(getGroups());
    }, [dispatch]);

    const handleCreateGroup = () => {
        if (groupName.trim()) {
            dispatch(createGroup({ name: groupName }));
            setGroupName("");
        }
    };

    return (
        <div className="container mx-auto p-4">
            <div className="flex flex-col items-center">
                <TextField
                    label="Tên nhóm"
                    variant="outlined"
                    value={groupName}
                    onChange={(e) => setGroupName(e.target.value)}
                    className="mb-4"
                />
                <Button variant="contained" color="primary" onClick={handleCreateGroup}>
                    Tạo Nhóm
                </Button>
            </div>

            {loading ? (
                <p className="text-center mt-4">Đang tải...</p>
            ) : (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mt-6">
                    {groups.map((group) => (
                        <Card key={group.id} className="shadow-lg">
                            <CardContent>
                                <Typography variant="h6">{group.name}</Typography>
                                <Button
                                    variant="contained"
                                    color="secondary"
                                    className="mt-2"
                                    onClick={() => dispatch(joinGroup(group.id))}
                                >
                                    Tham gia nhóm
                                </Button>
                            </CardContent>
                        </Card>
                    ))}
                </div>
            )}
        </div>
    );
};

export default Groups;
