import React, { useState, useEffect } from "react";
import {
    Container, Box, Typography, TextField, Button, Divider, Link,
    MenuItem, Select, FormControl, InputLabel
} from "@mui/material";
import { GoogleOAuthProvider, GoogleLogin } from "@react-oauth/google";
import FacebookLogin from "react-facebook-login/dist/facebook-login-render-props";
import { useFormik } from "formik";
import * as Yup from "yup";
import { useDispatch, useSelector } from "react-redux";
import { loginUser, registerUser, googleAuth, facebookAuth } from "../../Store/Auth/Action";

function Authentication() {
    const [isLogin, setIsLogin] = useState();
    const authError = useSelector((state) => state.auth.error);
    const [error, setError] = useState("");
    const dispatch = useDispatch();

    useEffect(() => {
        setIsLogin(window.location.pathname === "/login");
    }, []);

    const days = Array.from({ length: 31 }, (_, i) => i + 1);
    const months = Array.from({ length: 12 }, (_, i) => i + 1);
    const years = Array.from({ length: 100 }, (_, i) => new Date().getFullYear() - i);

    const validationSchema = Yup.object().shape({
        email: Yup.string().email("Email không hợp lệ").required("Bắt buộc nhập email"),
        password: Yup.string().min(6, "Mật khẩu phải có ít nhất 6 ký tự").required("Bắt buộc nhập mật khẩu"),
        fullName: isLogin ? Yup.string() : Yup.string().required("Bắt buộc nhập tên đầy đủ"),
        date: isLogin ? Yup.string() : Yup.string().required("Chọn ngày"),
        month: isLogin ? Yup.string() : Yup.string().required("Chọn tháng"),
        year: isLogin ? Yup.string() : Yup.string().required("Chọn năm"),
    });

    const formik = useFormik({
        initialValues: { fullName: "", email: "", password: "", date: "", month: "", year: "" },
        validationSchema,
        onSubmit: (values) => {
            if (isLogin) {
                dispatch(loginUser({ email: values.email, password: values.password }));
            } else {
                const birthDate = `${values.date}/${values.month}/${values.year}`;
                dispatch(registerUser({ email: values.email, password: values.password, fullName: values.fullName, birthDate }));
            }
        },
    });

    useEffect(() => {
        if (authError && authError !== "Request failed with status code 500") {
            setError(authError);
        }
    }, [authError]);

    // Xử lý đăng nhập Google
    const handleGoogleLogin = (response) => {
        if (response?.credential) {
            dispatch(googleAuth(response.credential));
        }
    };

    // Xử lý đăng nhập Facebook
    const handleFacebookLogin = (response) => {
        if (response?.accessToken) {
            dispatch(facebookAuth(response.accessToken));
        }
    };

    return (
        <GoogleOAuthProvider clientId="YOUR_GOOGLE_CLIENT_ID">
            <Container maxWidth={false} sx={{ width: "100vw", display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center", minHeight: "100vh", bgcolor: "black" }}>
                <Box sx={{ width: "100%", maxWidth: 400, textAlign: "center", p: 3, bgcolor: "black", color: "white", borderRadius: 2, boxShadow: 3, border: "1px solid #ccc", marginTop: "20px" }}>
                    <Typography variant="h4" fontWeight="bold" gutterBottom>Instagram</Typography>
                    <Typography variant="body2" gutterBottom>
                        {isLogin ? "Đăng nhập vào tài khoản của bạn." : "Đăng ký để xem ảnh và video từ bạn bè."}
                    </Typography>

                    {/* Facebook Login */}
                    <FacebookLogin
                        appId="YOUR_FACEBOOK_APP_ID"
                        autoLoad={false}
                        fields="name,email,picture"
                        callback={handleFacebookLogin}
                        render={(renderProps) => (
                            <Button fullWidth variant="contained" onClick={renderProps.onClick} sx={{ bgcolor: "#1877F2", color: "white", mb: 2 }}>
                                {isLogin ? "Đăng nhập bằng Facebook" : "Đăng ký bằng Facebook"}
                            </Button>
                        )}
                    />

                    {/* Google Login */}
                    <GoogleLogin onSuccess={handleGoogleLogin} onError={() => console.log("Google Login Failed")} />

                    <Divider sx={{ my: 2 }}>HOẶC</Divider>
                    {error && <Typography color="error" sx={{ mt: 2 }}>{error}</Typography>}

                    {/* Form đăng nhập/đăng ký */}
                    <Box component="form" noValidate autoComplete="off" onSubmit={formik.handleSubmit}>
                        {!isLogin && (
                            <>
                                <TextField
                                    fullWidth label="Tên đầy đủ" variant="filled" name="fullName"
                                    value={formik.values.fullName} onChange={formik.handleChange} onBlur={formik.handleBlur}
                                    error={formik.touched.fullName && Boolean(formik.errors.fullName)}
                                    helperText={formik.touched.fullName && formik.errors.fullName}
                                    sx={{ mb: 2, bgcolor: "#333" }} InputLabelProps={{ style: { color: "white" } }}
                                />

                                <Box sx={{ display: "flex", justifyContent: "space-between", gap: 1 }}>
                                    <FormControl fullWidth variant="filled" sx={{ bgcolor: "#333" }}>
                                        <InputLabel sx={{ color: "white" }}>Ngày</InputLabel>
                                        <Select name="date" value={formik.values.date} onChange={formik.handleChange} sx={{ color: "white" }}>
                                            {days.map((d) => (<MenuItem key={d} value={d}>{d}</MenuItem>))}
                                        </Select>
                                    </FormControl>
                                    <FormControl fullWidth variant="filled" sx={{ bgcolor: "#333" }}>
                                        <InputLabel sx={{ color: "white" }}>Tháng</InputLabel>
                                        <Select name="month" value={formik.values.month} onChange={formik.handleChange} sx={{ color: "white" }}>
                                            {months.map((m) => (<MenuItem key={m} value={m}>{m}</MenuItem>))}
                                        </Select>
                                    </FormControl>
                                    <FormControl fullWidth variant="filled" sx={{ bgcolor: "#333" }}>
                                        <InputLabel sx={{ color: "white" }}>Năm</InputLabel>
                                        <Select name="year" value={formik.values.year} onChange={formik.handleChange} sx={{ color: "white" }}>
                                            {years.map((y) => (<MenuItem key={y} value={y}>{y}</MenuItem>))}
                                        </Select>
                                    </FormControl>
                                </Box>
                            </>
                        )}

                        <TextField fullWidth label="Email" variant="filled" name="email" value={formik.values.email} onChange={formik.handleChange} sx={{ mt: 2, bgcolor: "#333" }} InputLabelProps={{ style: { color: "white" } }} />
                        <TextField fullWidth label="Mật khẩu" type="password" variant="filled" name="password" value={formik.values.password} onChange={formik.handleChange} sx={{ mt: 2, bgcolor: "#333" }} />

                        <Button fullWidth type="submit" variant="contained" sx={{ mt: 2, bgcolor: "#1877F2", color: "white" }}>
                            {isLogin ? "Đăng nhập" : "Đăng ký"}
                        </Button>
                    </Box>
                </Box>
            </Container>
        </GoogleOAuthProvider>
    );
}

export default Authentication;
