import React, { useState, useEffect } from "react";
import { Container, Box, Typography, TextField, Button, Divider, Link } from "@mui/material";
import FacebookIcon from "@mui/icons-material/Facebook";
import { GoogleOAuthProvider, GoogleLogin } from "@react-oauth/google";
import { useFormik } from "formik";
import * as Yup from "yup";

function Authentication() {
    const [isLogin, setIsLogin] = useState(false);

    useEffect(() => {
        const path = window.location.pathname;
        setIsLogin(path === "/login");
    }, []);

    const validationSchema = Yup.object().shape({
        email: Yup.string()
            .email("Email không hợp lệ")
            .required("Bắt buộc nhập email"),
        password: Yup.string()
            .min(6, "Mật khẩu phải có ít nhất 6 ký tự")
            .required("Bắt buộc nhập mật khẩu"),
        fullName: isLogin ? Yup.string() : Yup.string().required("Bắt buộc nhập tên đầy đủ"),
        username: isLogin ? Yup.string() : Yup.string().required("Bắt buộc nhập tên người dùng"),
    });

    const formik = useFormik({
        initialValues: {
            email: "",
            password: "",
            fullName: "",
            username: "",
        },
        validationSchema,
        onSubmit: (values) => {
            console.log("Dữ liệu form:", values);
        },
    });

    return (
        <GoogleOAuthProvider clientId="YOUR_GOOGLE_CLIENT_ID">
            <Container maxWidth={false} sx={{ width: "100vw", display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center", minHeight: "100vh", bgcolor: "black" }}>
                <Box sx={{ width: "100%", maxWidth: 400, textAlign: "center", p: 3, bgcolor: "black", color: "white", borderRadius: 2, boxShadow: 3, border: "1px solid #ccc", marginTop: "20px" }}>
                    <Typography variant="h4" fontWeight="bold" gutterBottom>
                        Instagram
                    </Typography>
                    <Typography variant="body2" gutterBottom>
                        {isLogin ? "Đăng nhập vào tài khoản của bạn." : "Đăng ký để xem ảnh và video từ bạn bè."}
                    </Typography>
                    <Button fullWidth variant="contained" startIcon={<FacebookIcon />} sx={{ bgcolor: "#1877F2", color: "white", mb: 2 }}>
                        {isLogin ? "Đăng nhập bằng Facebook" : "Đăng ký bằng Facebook"}
                    </Button>
                    <GoogleLogin
                        onSuccess={(credentialResponse) => {
                            console.log("Google Login Success:", credentialResponse);
                        }}
                        onError={() => {
                            console.log("Google Login Failed");
                        }}
                    />
                    <Divider sx={{ my: 2 }}>HOẶC</Divider>
                    <Box component="form" noValidate autoComplete="off" onSubmit={formik.handleSubmit}>
                        {!isLogin && (
                            <>
                                <TextField
                                    fullWidth
                                    label="Tên đầy đủ"
                                    variant="filled"
                                    name="fullName"
                                    value={formik.values.fullName}
                                    onChange={formik.handleChange}
                                    onBlur={formik.handleBlur}
                                    error={formik.touched.fullName && Boolean(formik.errors.fullName)}
                                    helperText={formik.touched.fullName && formik.errors.fullName}
                                    sx={{ mb: 2, bgcolor: "#333" }}
                                    InputLabelProps={{ style: { color: "white" } }}
                                />
                                <TextField
                                    fullWidth
                                    label="Tên người dùng"
                                    variant="filled"
                                    name="username"
                                    value={formik.values.username}
                                    onChange={formik.handleChange}
                                    onBlur={formik.handleBlur}
                                    error={formik.touched.username && Boolean(formik.errors.username)}
                                    helperText={formik.touched.username && formik.errors.username}
                                    sx={{ mb: 2, bgcolor: "#333" }}
                                    InputLabelProps={{ style: { color: "white" } }}
                                />
                            </>
                        )}
                        <TextField
                            fullWidth
                            label="Email"
                            variant="filled"
                            name="email"
                            value={formik.values.email}
                            onChange={formik.handleChange}
                            onBlur={formik.handleBlur}
                            error={formik.touched.email && Boolean(formik.errors.email)}
                            helperText={formik.touched.email && formik.errors.email}
                            sx={{ mb: 2, bgcolor: "#333" }}
                            InputLabelProps={{ style: { color: "white" } }}
                        />
                        <TextField
                            fullWidth
                            label="Mật khẩu"
                            type="password"
                            variant="filled"
                            name="password"
                            value={formik.values.password}
                            onChange={formik.handleChange}
                            onBlur={formik.handleBlur}
                            error={formik.touched.password && Boolean(formik.errors.password)}
                            helperText={formik.touched.password && formik.errors.password}
                            sx={{ mb: 2, bgcolor: "#333" }}
                            InputLabelProps={{ style: { color: "white" } }}
                        />
                        <Button fullWidth type="submit" variant="contained" sx={{ mt: 2, bgcolor: "#1877F2", color: "white" }}>
                            {isLogin ? "Đăng nhập" : "Đăng ký"}
                        </Button>
                    </Box>
                    <Typography variant="body2" sx={{ mt: 2 }}>
                        {isLogin ? (
                            <>Bạn chưa có tài khoản? <Link href="/signup" color="primary">Đăng ký</Link></>
                        ) : (
                            <>Bạn có tài khoản? <Link href="/login" color="primary">Đăng nhập</Link></>
                        )}
                    </Typography>
                </Box>
            </Container>
        </GoogleOAuthProvider>
    );
}

export default Authentication;
