import React, { useEffect, useState } from "react";
import { Button, TextField, Card, CardContent, Typography, CircularProgress } from "@mui/material";
import { useDispatch, useSelector } from "react-redux";
import { createGroup, getGroups, joinGroup } from "../../Store/Group/Action";
import { motion } from "framer-motion";

const Groups = () => {
    const dispatch = useDispatch();
    const { groups, loading } = useSelector((state) => state.group);
    const [groupName, setGroupName] = useState("");

    useEffect(() => {
        dispatch(getGroups());
    }, [dispatch]);

    const handleCreateGroup = () => {
        if (groupName.trim()) {
            dispatch(createGroup({ name: groupName }));
            setGroupName("");
        }
    };

    return (
        <div className="container mx-auto p-4">
            <div className="flex flex-col items-center">
                <motion.div
                    initial={{ opacity: 0, y: -20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.5 }}
                    className="w-full max-w-md"
                >
                    <TextField
                        fullWidth
                        label="Tên nhóm"
                        variant="outlined"
                        value={groupName}
                        onChange={(e) => setGroupName(e.target.value)}
                        className="mb-4"
                        InputProps={{
                            style: {
                                borderRadius: "12px",
                            },
                        }}
                    />
                    <Button
                        fullWidth
                        variant="contained"
                        color="primary"
                        onClick={handleCreateGroup}
                        style={{
                            borderRadius: "12px",
                            padding: "12px",
                            fontSize: "16px",
                            fontWeight: "bold",
                        }}
                    >
                        Tạo Nhóm
                    </Button>
                </motion.div>
            </div>

            {loading ? (
                <div className="flex justify-center mt-8">
                    <CircularProgress color="primary" />
                </div>
            ) : (
                <motion.div
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    transition={{ duration: 0.5 }}
                    className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mt-8"
                >
                    {groups.map((group) => (
                        <motion.div
                            key={group.id}
                            whileHover={{ scale: 1.05 }}
                            whileTap={{ scale: 0.95 }}
                        >
                            <Card className="shadow-lg rounded-lg overflow-hidden">
                                <CardContent className="p-6">
                                    <Typography variant="h6" className="font-bold mb-4">
                                        {group.name}
                                    </Typography>
                                    <Button
                                        fullWidth
                                        variant="contained"
                                        color="secondary"
                                        onClick={() => dispatch(joinGroup(group.id))}
                                        style={{
                                            borderRadius: "12px",
                                            padding: "10px",
                                            fontSize: "14px",
                                            fontWeight: "bold",
                                        }}
                                    >
                                        Tham gia nhóm
                                    </Button>
                                </CardContent>
                            </Card>
                        </motion.div>
                    ))}
                </motion.div>
            )}
        </div>
    );
};

export default Groups;
