import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import RepeatIcon from '@mui/icons-material/Repeat';
import { Avatar, Button, Menu, MenuItem, TextField } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { MoreHoriz } from '@mui/icons-material';
import ChatBubbleOutlineIcon from '@mui/icons-material/ChatBubbleOutline';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import FavoriteIcon from '@mui/icons-material/Favorite';
import ReplyIcon from '@mui/icons-material/Reply';
import { deletePost, likePost, createPostReply } from '../../Store/Post/Action';

function PostCard({ post }) {
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const [anchorEl, setAnchorEl] = useState();
    const open = Boolean(anchorEl);
    const [comment, setComment] = useState('');
    const [comments, setComments] = useState([]);

    const likedPosts = useSelector(state => state.post.likedPosts);
    const isLiked = likedPosts.some(likedPost => likedPost.id === post.id);

    const handleClick = (e) => {
        setAnchorEl(e.currentTarget);
    };

    const handleClose = () => {
        setAnchorEl(null);
    };

    const handleDeletePost = () => {
        dispatch(deletePost(post?.id));
        handleClose();
    };

    const handleLikePost = () => {
        dispatch(likePost(post?.id));
    };

    const handleAddComment = () => {
        if (comment.trim() !== '') {
            dispatch(createPostReply({ content: comment, postId: post.id }));
            setComments([...comments, comment]);
            setComment('');
        }
    };

    return (
        <div className=''>
            <div className='flex space-x-5'>
                <Avatar
                    onClick={() => navigate(`/profile/${post?.user?._id}`)}
                    className='cursor-pointer'
                    alt='username'
                    src={post?.user?.avatar || 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRSNNk7980my9X3TSDyVntzFhngNX4ZwpZI6g&s'}
                />
                <div className='w-full'>
                    <div className='flex justify-between items-center'>
                        <div className='flex cursor-pointer items-center space-x-2'>
                            <span className="font-semibold">{post?.user?.fullName}</span>
                            <span className="text-gray-600">@{post?.user?.email} · 2m</span>
                            <img className='ml-2 w-5 h-5' src="https://cdn-icons-png.flaticon.com/512/6364/6364343.png" alt="content-image" />
                        </div>
                        <div>
                            <Button
                                id="demo-positioned-button"
                                aria-controls={open ? 'demo-positioned-menu' : undefined}
                                aria-haspopup="true"
                                aria-expanded={open ? 'true' : undefined}
                                onClick={handleClick}
                            >
                                <MoreHoriz />
                            </Button>
                            <Menu
                                id="demo-positioned-menu"
                                anchorEl={anchorEl}
                                open={open}
                                onClose={handleClose}
                                anchorOrigin={{ vertical: 'top', horizontal: 'left' }}
                                transformOrigin={{ vertical: 'top', horizontal: 'left' }}
                            >
                                <MenuItem onClick={handleDeletePost}>Delete</MenuItem>
                                <MenuItem onClick={handleDeletePost}>Edit</MenuItem>
                            </Menu>
                        </div>
                    </div>
                    <div className='mt-2'>
                        <div className='cursor-pointer text-left'>
                            <p className="mt-2">{post?.content}</p>
                            {post?.image && (
                                <img className='w-[28rem] border border-gray-400 p-5 rounded-md'
                                    src={post?.image} alt="post-image" />
                            )}
                        </div>
                        <div className='py-5 flex items-center justify-between text-gray-600'>
                            <div className='flex space-x-5'>
                                <div className='flex items-center space-x-2 cursor-pointer hover:text-blue-500 transition'>
                                    <ChatBubbleOutlineIcon fontSize="small" />
                                    <p className='text-sm'>{post?.totalReplies}</p>
                                </div>

                                <div onClick={handleLikePost} className='cursor-pointer flex items-center space-x-2 hover:text-red-500 transition'>
                                    {isLiked ? <FavoriteIcon fontSize="small" color="error" />
                                        : <FavoriteBorderIcon fontSize="small" />}
                                    <p className='text-sm'>{post.totalLikes}</p>
                                </div>

                                <div className='flex items-center space-x-2 cursor-pointer hover:text-green-500 transition'>
                                    <RepeatIcon fontSize="small" />
                                    <p className='text-sm'>Share</p>
                                </div>
                            </div>
                        </div>
                        <div className='mt-4'>
                            <TextField
                                fullWidth
                                variant="outlined"
                                size="small"
                                placeholder="Viết bình luận..."
                                value={comment}
                                onChange={(e) => setComment(e.target.value)}
                            />
                            <Button onClick={handleAddComment} className='mt-2' variant='contained' color='primary'>Bình luận</Button>
                            <div className='mt-3'>
                                {comments.map((cmt, index) => (
                                    <div key={index} className='border-b py-2'>
                                        <p>{cmt}</p>
                                    </div>
                                ))}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default PostCard;
