import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { createReel, getReels } from "../../Store/Reel/Action";
import { Button, CircularProgress, Modal, TextField } from "@mui/material";
import { ArrowBackIos, ArrowForwardIos, Add } from "@mui/icons-material";

const Reel = () => {
    const dispatch = useDispatch();
    const { reels, loading, error } = useSelector((state) => state.reel);

    const [file, setFile] = useState(null);
    const [description, setDescription] = useState("");
    const [currentIndex, setCurrentIndex] = useState(0);
    const [isModalOpen, setIsModalOpen] = useState(false);

    useEffect(() => {
        dispatch(getReels());
    }, [dispatch]);

    const handleCreateReel = () => {
        if (!file) return alert("Vui lòng chọn file!");

        const formData = new FormData();
        formData.append("file", file);
        formData.append("description", description);

        dispatch(createReel({ file, description }));
        setIsModalOpen(false); // Đóng modal sau khi tạo reel
    };

    const handleNext = () => {
        if (currentIndex < reels.length - 1) {
            setCurrentIndex(currentIndex + 1);
        }
    };

    const handlePrev = () => {
        if (currentIndex > 0) {
            setCurrentIndex(currentIndex - 1);
        }
    };

    if (loading) return <CircularProgress />;
    if (error) return <p className="text-red-500">Lỗi: {error}</p>;

    return (
        <div className="relative w-full h-screen flex items-center justify-center bg-black">
            {/* Nút thêm Reel */}
            <Button
                onClick={() => setIsModalOpen(true)}
                className="absolute top-4 right-4 bg-blue-500 text-white p-2 rounded-full flex items-center">
                <Add />
                Thêm Reel
            </Button>

            {reels.length > 0 ? (
                <div className="relative w-[400px] h-[600px] bg-white rounded-lg overflow-hidden shadow-lg">
                    {reels[currentIndex]?.image?.toLowerCase().endsWith(".mp4") ? (
                        <video src={reels[currentIndex].image} controls className="w-full h-full object-cover" />
                    ) : (
                        <img src={reels[currentIndex].image} alt="Reel" className="w-full h-full object-cover" />
                    )}

                    {currentIndex > 0 && (
                        <Button onClick={handlePrev} className="absolute left-4 bg-gray-500 text-white p-2 rounded-full">
                            <ArrowBackIos />
                        </Button>
                    )}
                    {currentIndex < reels.length - 1 && (
                        <Button onClick={handleNext} className="absolute right-4 bg-gray-500 text-white p-2 rounded-full">
                            <ArrowForwardIos />
                        </Button>
                    )}
                </div>
            ) : (
                <p className="text-white text-center">Không có reel nào.</p>
            )}

            {/* Modal thêm Reel */}
            <Modal open={isModalOpen} onClose={() => setIsModalOpen(false)}>
                <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 bg-white p-6 rounded-lg shadow-lg w-[400px]">
                    <h2 className="text-lg font-semibold mb-4">Thêm Reel Mới</h2>
                    <input type="file" onChange={(e) => setFile(e.target.files[0])} className="mb-4 w-full" />
                    <TextField
                        label="Mô tả"
                        fullWidth
                        multiline
                        rows={2}
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                        className="mb-4"
                    />
                    <div className="flex justify-end">
                        <Button onClick={() => setIsModalOpen(false)} className="mr-2 bg-gray-400 text-white p-2 rounded">
                            Hủy
                        </Button>
                        <Button onClick={handleCreateReel} className="bg-blue-500 text-white p-2 rounded">
                            Thêm
                        </Button>
                    </div>
                </div>
            </Modal>
        </div>
    );
};

export default Reel;
