import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { Avatar, Badge, Button, Menu, MenuItem } from "@mui/material";
import { MoreHoriz } from "@mui/icons-material";
import logo from "../../images/avatar/logo.png";
import { useDispatch, useSelector } from "react-redux";
import { logout } from "../../Store/Auth/Action";
import { navigationMenu } from "./NavigationMenu";

const Navigation = () => {
    const { auth } = useSelector((store) => store);
    const notifications = useSelector((store) => store.chat.notifications);
    const dispatch = useDispatch();
    const navigate = useNavigate();

    const id = auth.user.id;

    const [anchorEl, setAnchorEl] = useState(null);
    const open = Boolean(anchorEl);

    const handleMenuClick = (event) => setAnchorEl(event.currentTarget);
    const handleClose = () => setAnchorEl(null);
    const handleLogout = () => {
        dispatch(logout());
        handleClose();
    };

    useEffect(() => {
        console.log("Danh sách thông báo:", notifications);
    }, [notifications]);

    return (
        <div className="h-screen sticky top-0 flex flex-col justify-between bg-white shadow-md w-[280px] px-6 py-6">
            <div className="flex justify-center">
                <img src={logo} alt="logo" className="w-[60px] h-[60px] rounded-full" />
            </div>

            <div className="flex flex-col space-y-4 mt-8">
                {navigationMenu.map((item, index) => (
                    <div
                        key={index}
                        className="cursor-pointer flex items-center gap-4 p-3 rounded-lg hover:bg-gray-200 transition"
                        onClick={() => navigate(item.path)}
                    >
                        {item.title === "Thông báo" ? (
                            <Badge badgeContent={notifications[id] || 0} color="error">
                                {item.icon}
                            </Badge>
                        ) : (
                            item.icon
                        )}
                        <p className="text-lg font-medium">{item.title}</p>
                    </div>
                ))}
            </div>

            <div className="flex items-center gap-4 p-3 bg-gray-100 rounded-lg cursor-pointer">
                <Avatar
                    alt={auth.user?.fullName || "User"}
                    src="https://cdn-icons-png.flaticon.com/512/8345/8345328.png"
                    onClick={() => navigate("/account")}
                />
                <div className="flex flex-col">
                    <p className="text-sm font-semibold">{auth.user?.fullName}</p>
                    <span className="text-xs text-gray-500">
                        @{auth.user?.fullName ? auth.user.fullName.replace(/\s+/g, "_").toLowerCase() : "unknown_user"}
                    </span>
                </div>

                <Button onClick={handleMenuClick}>
                    <MoreHoriz />
                </Button>
                <Menu
                    anchorEl={anchorEl}
                    open={open}
                    onClose={handleClose}
                    anchorOrigin={{ vertical: "top", horizontal: "left" }}
                    transformOrigin={{ vertical: "top", horizontal: "left" }}
                >
                    <MenuItem onClick={handleLogout}>Đăng xuất</MenuItem>
                </Menu>
            </div>
        </div>
    );
};

export default Navigation;
