import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import KeyboardBackspaceIcon from '@mui/icons-material/KeyboardBackspace';
import { Avatar, Button } from '@mui/material';
import BusinessCenterIcon from '@mui/icons-material/BusinessCenter';
import LocationOnIcon from '@mui/icons-material/LocationOn';
import CalendarMonthIcon from '@mui/icons-material/CalendarMonth';
import Box from '@mui/material/Box';
import Tab from '@mui/material/Tab';
import TabContext from '@mui/lab/TabContext';
import TabList from '@mui/lab/TabList';
import TabPanel from '@mui/lab/TabPanel';
import PostCard from '../HomeSection/PostCard';

function Profile() {
    const { id } = useParams(); // Lấy id từ URL
    const navigate = useNavigate();
    const [user, setUser] = useState(null);
    const [value, setValue] = useState('1');

    useEffect(() => {
        const fetchUserProfile = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/api/user/profile/${id}`);
                setUser(response.data);
            } catch (error) {
                console.error("Error fetching user profile:", error);
            }
        };
        fetchUserProfile();
    }, [id]);

    const handleBack = () => {
        navigate(-1);
    };

    const handleChange = (event, newValue) => {
        setValue(newValue);
    };

    return (
        <div>
            <section className="z-50 flex items-center sticky top-0 bg-opacity-95">
                <KeyboardBackspaceIcon className='cursor-pointer' onClick={handleBack} />
                <h1 className='py-5 text-xl font-bold opacity-90 ml-5'>{user?.fullName || "Loading..."}</h1>
            </section>
            <section>
                <img className='w-[100%] h-[15rem] object-cover'
                    src={user?.backgroundImage || "https://www.anhrgroup.com/sites/default/files/styles/inner_pages_slideshow/public/basic-pages/al-nahda-samar-restaurant-1.jpg"}
                    alt="background" />
            </section>
            <section className='pl-6'>
                <div className='flex justify-between items-start mt-5 h-[5rem]'>
                    <Avatar className='transform -translate-y-24'
                        alt='avatar'
                        src={user?.image || "https://cdn-icons-png.flaticon.com/512/8345/8345328.png"}
                        sx={{ width: "10rem", height: "10rem", border: "4px solid white" }} />
                    <Button className='rounded-full' variant='contained' sx={{ borderRadius: "20px" }}>
                        Edit profile
                    </Button>
                </div>
                <div>
                    <h1 className='font-bold text-lg'>{user?.fullName}</h1>
                    <h1 className='text-gray-500'>@{user?.fullName?.toLowerCase().replace(/\s/g, '')}</h1>
                </div>
                <div className='mt-2 space-y-3'>
                    <p>{user?.bio || "Chưa có mô tả"}</p>
                    <div className='py-1 flex space-x-5'>
                        {user?.location && (
                            <div className='flex items-center'>
                                <LocationOnIcon />
                                <p className='ml-2'>{user.location}</p>
                            </div>
                        )}
                        <div className='flex items-center'>
                            <CalendarMonthIcon />
                            <p className='ml-2'>{user?.birthDate}</p>
                        </div>
                    </div>
                </div>
            </section>
            <section>
                <Box className="mt-4" sx={{ width: '100%', typography: 'body1' }}>
                    <TabContext value={value}>
                        <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
                            <TabList onChange={handleChange}>
                                <Tab label="Post" value="1" />
                                <Tab label="Image" value="2" />
                                <Tab label="Saved" value="3" />
                            </TabList>
                        </Box>
                        <TabPanel value="1">
                            <PostCard />
                        </TabPanel>
                        <TabPanel value="2">Item Two</TabPanel>
                        <TabPanel value="3">Item Three</TabPanel>
                    </TabContext>
                </Box>
            </section>
        </div>
    );
}

export default Profile;
