import React, { useEffect, useState } from "react";
import { Button, TextField, Card, CardContent, Typography, CircularProgress, Avatar } from "@mui/material";
import { useDispatch, useSelector } from "react-redux";
import { createGroup, getGroups, joinGroup } from "../../Store/Group/Action";
import moment from "moment"; // Thư viện để định dạng ngày tháng

const Groups = () => {
    const dispatch = useDispatch();
    const { groups, loading } = useSelector((state) => state.group);
    const [groupName, setGroupName] = useState("");

    useEffect(() => {
        dispatch(getGroups());
    }, [dispatch]);

    const handleCreateGroup = () => {
        if (groupName.trim()) {
            dispatch(createGroup({ name: groupName }));
            setGroupName("");
        }
    };

    return (
        <div className="container mx-auto p-4">
            <div className="flex flex-col items-center mb-8">
                <div className="w-full max-w-md bg-white p-6 rounded-lg shadow-md">
                    <Typography variant="h5" className="font-bold text-center mb-4">
                        Tạo Nhóm Mới
                    </Typography>
                    <TextField
                        fullWidth
                        label="Tên nhóm"
                        variant="outlined"
                        value={groupName}
                        onChange={(e) => setGroupName(e.target.value)}
                        className="mb-4"
                        InputProps={{
                            style: {
                                borderRadius: "8px",
                            },
                        }}
                    />
                    <Button
                        fullWidth
                        variant="contained"
                        color="primary"
                        onClick={handleCreateGroup}
                        style={{
                            borderRadius: "8px",
                            padding: "12px",
                            fontSize: "16px",
                            fontWeight: "bold",
                            textTransform: "none",
                        }}
                    >
                        Tạo Nhóm
                    </Button>
                </div>
            </div>

            {loading ? (
                <div className="flex justify-center mt-8">
                    <CircularProgress color="primary" />
                </div>
            ) : (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {groups.map((group) => (
                        <Card key={group.id} className="shadow-md rounded-lg hover:shadow-lg transition-shadow duration-300">
                            <CardContent className="p-6">
                                <div className="flex items-center mb-4">
                                    <Avatar className="bg-blue-500 text-white mr-4">
                                        {group.name.charAt(0)}
                                    </Avatar>
                                    <div>
                                        <Typography variant="h6" className="font-bold">
                                            {group.name}
                                        </Typography>
                                        <Typography variant="body2" className="text-gray-500">
                                            Quản trị viên: {group.admin.name}
                                        </Typography>
                                    </div>
                                </div>
                                <Typography variant="body2" className="text-gray-500 mb-4">
                                    Ngày tạo: {moment(group.createdDate).format("DD/MM/YYYY")}
                                </Typography>
                                <Typography variant="body2" className="text-gray-500 mb-4">
                                    Số bài viết: {group.posts.length}
                                </Typography>
                                <Button
                                    fullWidth
                                    variant="outlined"
                                    color="primary"
                                    onClick={() => dispatch(joinGroup(group.id))}
                                    style={{
                                        borderRadius: "8px",
                                        padding: "10px",
                                        fontSize: "14px",
                                        fontWeight: "bold",
                                        textTransform: "none",
                                    }}
                                >
                                    Tham gia nhóm
                                </Button>
                            </CardContent>
                        </Card>
                    ))}
                </div>
            )}
        </div>
    );
};

export default Groups;
